import { storage } from "@/firebase";
import "firebase/storage";
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";

export default {
  state: {
    firebaseUploadRes: [],
    firebaseDownloadRes: [],
  },
  getters: {
    getFirebaseUploadRes: (state) => {
      return state.firebaseUploadRes;
    },
    getFirebaseDownloadRes: (state) => {
        return state.firebaseDownloadRes
    }
  },
  mutations: {
    setFirebaseUploadRes: (state, value) => {
      state.firebaseUploadRes = value;
    },
    setFirebaseDownloadRes: (state, value) => {
        state.firebaseDownloadRes = value
    }
  },
  actions: {
    async FIREBASE_UPLOAD({ commit }, {fileName}) {
      const storageRef = ref(storage, `files/${fileName}`);
      const response = await uploadBytes(storageRef, this.$refs.audioinput.files[0])
      commit('setFirebaseUploadRes', response.metadata)
    },
    async FIREBASE_DOWNLOAD({commit}, {fileName}) {
        const storageRef = ref(storage, `Images/${this.fileName}`)
        const response = getDownloadURL().then((res) => {
            this.url = res;
            console.log(this.url);
          });
    }
  },
};
