import QuestionType from "../components/QuestionType.vue";
import QuestionComponent from "../components/QuestionComponent.vue";
import AnswerTypeComponent from "../components/AnswerTypeComponent.vue";
import AnswerComponent from "../components/AnswerComponent.vue";
export default {
  name: "AddQuestionView",
  data() {
    return {
      next: 0,
      questionType: true,
      question: false,
      answerType: false,
      answer: false
    };
  },
  components: {
    QuestionType,
    QuestionComponent,
    AnswerTypeComponent,
    AnswerComponent
  },
  methods: {
    nextHandler() {
      if (this.next === 0) {
        console.log("In question Type");
        this.questionType = !this.questionType, this.question = !this.question;
        this.next++;
      } else if (this.next === 1) {
        console.log("In question Type");
        this.question = !this.question, this.answerType = !this.answerType, this.next++;
      } else if (this.next === 2) {
        this.answerType = !this.answerType, this.answer = !this.answer, this.next++;
      } else {
        this.answer = !this.answerType;
        this.next = 0;
      }
    },
    submitHandler() {
      console.log("Done");
      this.questionType = !this.questionType;
      // console.log("questionType: ", this.questionType)
      // console.log("answerType: ", this.questionType)
      this.answer = !this.answer;
      this.next = 0;
    }
  }
};
